Section.create(:id => 1, :name => "blog", :content => "<%= get_snippet('layout_header') %>\r\n  \r\n  <% articles = Article.page(params[:page]).per(12)%>\r\n    \r\n    <% articles.each do |article|%>\r\n      <div class=\"box post\">\r\n        <div class=\"content\">\r\n          <div class=\"commets\">\r\n            <a href=\"<%= section_article_path(section_slug('blog_article'),article.slug)%>\" title=\"View Comments\">\r\n              <%= article.comments.size %>\r\n            </a>\r\n          </div>\r\n          <!-- commets -->\r\n          <div class=\"post-title\">\r\n            <h2>\r\n              <%= article.title %>\r\n              </h2>\r\n            </div>\r\n            <!--/post-title -->\r\n            <div class=\"social-links\">\r\n              <div class=\"post-date\">\r\n                <%= article.display_date%>\r\n                  , In\r\n                  <%= tags_with_links(article, 'blog_tag') %>\r\n                    , by \r\n                    <%= author_with_links(article, 'blog_author')%>\r\n                    </div>\r\n                    <div class=\"clr\">\r\n                    </div>\r\n                  </div>\r\n                  <!--/social-links -->\r\n                  <div class=\"clr\">\r\n                  </div>\r\n                  <div class=\"article-content\">\r\n                    <% if article.excerpt.empty? %>\r\n                      <%= article.display_content %>\r\n                        <p>\r\n                          <a class=\"readmore\" href=\"<%= section_article_path(section_slug('blog_article'),article.slug)%>\">View Comments&raquo;</a>\r\n                     </p>\r\n                        <% else %>\r\n                          <%= article.display_excerpt %>\r\n                            <p>\r\n                              <a class=\"readmore\" href=\"<%= section_article_path(section_slug('blog_article'),article.slug)%>\">Read more &raquo;</a>\r\n                     </p>\r\n                     <% end %>\r\n                     </div>\r\n                     <!--/post-excerpt -->\r\n                  </div>\r\n                  <!--/content -->\r\n                  <div class=\"clr\">\r\n                  </div>\r\n                   </div>\r\n                   <div class=\"clr\">\r\n                   </div>\r\n                   <!--/box -->\r\n                   <% end %>\r\n                     \r\n                     <div class=\"clr\">\r\n                     </div>\r\n                     <div class=\"wp-pagenavi\">\r\n                       \r\n                       <span class=\"pages\">\r\n                         <%= pages(articles)%>\r\n                         </span>\r\n                         \r\n                         <%= paginate(articles)%>\r\n                           \r\n                           <div class=\"clr\">\r\n                           </div>\r\n                         </div>\r\n                       </div>\r\n                       <%= get_snippet('layout_recent_entries_box') %>\r\n                         <%= get_snippet('layout_categories_box') %>\r\n                           <%= get_snippet('layout_archives_box') %>\r\n                             <%= get_snippet('layout_footer') %>\r\n                               \r\n                               ", :slug => "blog")
Section.create(:id => 2, :name => "blog_article", :content => "<%= get_snippet('layout_header') %>\r\n  \r\n  <% article = Article.find_by_slug(params[:article]) %>\r\n    \r\n    <% if article %>\r\n      <div class=\"box post\">\r\n        <div class=\"content\">\r\n          <div class=\"commets\">\r\n            <a href=\"<%= section_article_path(section_slug('blog_article'),article.slug)%>\" title=\"View Comments\">\r\n              <%= article.comments.size %>\r\n              </a>\r\n            </div>\r\n            <!-- commets -->\r\n            <div class=\"post-title\">\r\n              <h2>\r\n                <%= article.title %>\r\n                </h2>\r\n              </div>\r\n              <!--/post-title -->\r\n              <div class=\"social-links\">\r\n                <div class=\"post-date\">\r\n                  <%= article.display_date%>\r\n                    , In\r\n                    <%= tags_with_links(article, 'blog_tag') %>\r\n                      , by \r\n                      <%= author_with_links(article, 'blog_author')%>\r\n                      </div>\r\n                      <div class=\"clr\">\r\n                      </div>\r\n                    </div>\r\n                    <!--/social-links -->\r\n                    <div class=\"clr\">\r\n                    </div>\r\n                    <div class=\"article-content\">\r\n                      <%= article.display_excerpt %>\r\n                        <%= article.display_content %>\r\n                        </div>\r\n                        <!--/post-excerpt -->\r\n                        \r\n                        <div class=\"article-content\" style=\"padding:15px;background-color:#F5F5F5;\">\r\n                          <p>\r\n                            <b>\r\n                              Leave a Comment:\r\n                            </b>\r\n                          </p>\r\n                          <span style=\"color:red;\">\r\n                            <%= flash[:error] %>\r\n                            </span>\r\n                            <form action=\"\r\n<%= create_comment_url%>\r\n\" method=\"post\">\r\n  <table>\r\n    <tr>\r\n      <td width=\"70\">\r\n        <span style=\"color:red;\">*</span><em>Name:</em>\r\n      </td>\r\n      <td width=\"100\">\r\n        <input type=\"text\" name=\"comment[name]\" value=\"<%= session[:comment_name]%>\" size=\"35\" maxlength=\"50\" />\r\n      </td>\r\n    </tr>\r\n    <tr>\r\n      <td>\r\n        <em>\r\n          Email:\r\n        </em>\r\n      </td>\r\n      <td>\r\n        <input type=\"text\" name=\"comment[email]\" value=\"<%= session[:comment_email]%>\" size=\"35\" maxlength=\"50\" />\r\n      </td>\r\n    </tr>\r\n    <tr>\r\n      <td>\r\n        <span style=\"color:red;\">*</span><em>Comment:</em>\r\n      </td>\r\n      <td>\r\n        <textarea name=\"comment[content]\" rows=8 cols=50><%= session[:comment_content]%></textarea>\r\n       </td>\r\n    </tr>\r\n    <tr>\r\n      <tr>\r\n        <td>\r\n          <span style=\"color:red;\">*</span><em>Verify:</em>\r\n          </td>\r\n          <td>\r\n            <table>\r\n              <tr>\r\n              <td><%= captcha %></td>\r\n                <td>  <input type=\"text\" name=\"captchator\",  value=\"\" size=\"5\" maxlength=\"5\" /></td>\r\n              </tr>\r\n            </table>\r\n        </td>\r\n    </tr>\r\n    <td>\r\n      <input type=\"hidden\" name=\"comment[article_id]\" value=\"\r\n<%= article.id %>\r\n\" />\r\n      <input type=\"hidden\" name=\"return_path\" value=\"\r\n<%= section_article_path(section_slug('blog_article'),article.slug)%>\r\n\" />\r\n    </td>\r\n    <td align=\"right\">\r\n      <input type=\"submit\" name=\"submit\" value=\"Submit\" />\r\n    </td>\r\n  </tr>\r\n                          </table>\r\n                        </form>\r\n                        \r\n                      </div>\r\n                      <div style=\"padding:15px;margin-top:10px;\" class=\"article-content\">\r\n                        <% article.comments.each do |comment| %>\r\n                          <div style=\"margin-top:20px;\">\r\n                            <div>\r\n                              <div style=\"background-color:beige;padding:20px;\">\r\n                                <%= simple_format comment.content %>\r\n                                </div>\r\n                                <div style=\"background-color:white;text-align:right;padding:10px;\">\r\n                                  <p>\r\n                                    By \r\n                                    <b>\r\n                                      <em>\r\n                                        <%= comment.name %>\r\n                                        </em>\r\n                                      </b>\r\n                                      on \r\n                                      <em>\r\n                                        <%= comment.display_date%>\r\n                                        </em>\r\n                                        \r\n                                      </p>\r\n                                    </div>\r\n                                  </div>\r\n                              </div>\r\n                              \r\n                              <% end %>\r\n                                \r\n                              </div>\r\n                              \r\n                            </div>\r\n                            <!--/content -->\r\n                            <div class=\"clr\">\r\n                            </div>\r\n                          </div>\r\n                          <div class=\"clr\">\r\n                          </div>\r\n                          <!--/box -->\r\n                          \r\n                          <% else %>\r\n                            <h2>\r\n                              No Article Found\r\n                            </h2>\r\n                            <% end %>\r\n                              \r\n                              <div class=\"clr\">\r\n                              </div>\r\n                            </div>\r\n                            \r\n                           <%= get_snippet('layout_recent_entries_box') %>\r\n                         <%= get_snippet('layout_categories_box') %>\r\n                           <%= get_snippet('layout_archives_box') %>\r\n                             <%= get_snippet('layout_footer') %>", :slug => "blog_article")
Section.create(:id => 3, :name => "blog_author", :content => "<%= get_snippet('layout_header') %>\r\n  <% user = User.find_by_slug(params[:article]) %>\r\n  \r\n    <% if user %>\r\n      <% articles = Article.where(:user_id => user.id).page(params[:page]).per(12)%>\r\n  <% articles.each do |article|%>\r\n    <div class=\"box post\">\r\n      <div class=\"content\">\r\n        <div class=\"commets\">\r\n          <a href=\"<%= section_article_path(section_slug('blog_article'),article.slug)%>\" title=\"View Comments\">\r\n           <%= article.comments.size %>\r\n          </a>\r\n        </div>\r\n        <!-- commets -->\r\n        <div class=\"post-title\">\r\n          <h2>\r\n            <%= article.title %>\r\n            </h2>\r\n          </div>\r\n          <!--/post-title -->\r\n          <div class=\"social-links\">\r\n            <div class=\"post-date\">\r\n              <%= article.display_date%>\r\n                , In\r\n                <%= tags_with_links(article,'blog_tag') %>\r\n                , by \r\n               <%= author_with_links(article, 'blog_author')%>\r\n              </div>\r\n              <div class=\"clr\">\r\n                </div>\r\n              </div>\r\n              <!--/social-links -->\r\n              <div class=\"clr\">\r\n              </div>\r\n               <div class=\"article-content\">\r\n                 <% if article.excerpt.empty? %>\r\n                 <%= article.display_content %> \r\n                     <p>\r\n                          <a class=\"readmore\" href=\"<%= section_article_path(section_slug('blog_article'),article.slug)%>\">View Comments&raquo;</a>\r\n                     </p>\r\n                <% else %>\r\n                   <%= article.display_excerpt %>\r\n                  <p><a class=\"readmore\" href=\"<%= section_article_path(section_slug('blog_article'),article.slug)%>\">Read more &raquo;</a></p>\r\n                <% end %>\r\n                       </div>\r\n                       <!--/post-excerpt -->\r\n              </div>\r\n              <!--/content -->\r\n              <div class=\"clr\">\r\n              </div>\r\n              </div>\r\n              <div class=\"clr\">\r\n              </div>\r\n              <!--/box -->\r\n              <% end %>\r\n               <% else %>\r\n                 <h2>\r\n                   Author not found\r\n                 </h2>\r\n                 <% end %>\r\n                \r\n                <div class=\"clr\">\r\n                </div>\r\n                <div class=\"wp-pagenavi\">\r\n                  \r\n                   <span class=\"pages\">\r\n                   <%= pages(articles)%>\r\n                  </span>\r\n                    \r\n                  <%= paginate(articles)%>\r\n                  \r\n                  <div class=\"clr\">\r\n                  </div>\r\n                </div>\r\n              </div>\r\n              \r\n             <%= get_snippet('layout_recent_entries_box') %>\r\n                         <%= get_snippet('layout_categories_box') %>\r\n                           <%= get_snippet('layout_archives_box') %>\r\n                             <%= get_snippet('layout_footer') %>", :slug => "blog_author")
Section.create(:id => 4, :name => "blog_tag", :content => "<%= get_snippet('layout_header') %>\r\n  \r\n <% matching_articles = Article.find_by_tag(params[:article]).collect{|t| t.id} %>    \r\n    \r\n   <% if matching_articles %>\r\n     <% articles = Article.where(\"id in (\#{matching_articles.join(',')})\").page(params[:page]).per(12)%>\r\n      \r\n  <% articles.each do |article|%>\r\n    <div class=\"box post\">\r\n      <div class=\"content\">\r\n        <div class=\"commets\">\r\n          <a href=\"<%= section_article_path(section_slug('blog_article'),article.slug)%>\" title=\"View Comments\">\r\n            <%= article.comments.size %>\r\n          </a>\r\n        </div>\r\n        <!-- commets -->\r\n        <div class=\"post-title\">\r\n          <h2>\r\n            <%= article.title %>\r\n            </h2>\r\n          </div>\r\n          <!--/post-title -->\r\n          <div class=\"social-links\">\r\n            <div class=\"post-date\">\r\n              <%= article.display_date%>\r\n                , In\r\n                <%= tags_with_links(article,'blog_tag') %>\r\n                , by \r\n               <%= author_with_links(article, 'blog_author')%>\r\n              </div>\r\n              <div class=\"clr\">\r\n                </div>\r\n              </div>\r\n              <!--/social-links -->\r\n              <div class=\"clr\">\r\n              </div>\r\n               <div class=\"article-content\">\r\n                 <% if article.excerpt.empty? %>\r\n                 <%= article.display_content %> \r\n                     <p>\r\n                          <a class=\"readmore\" href=\"<%= section_article_path(section_slug('blog_article'),article.slug)%>\">View Comments&raquo;</a>\r\n                     </p>\r\n                <% else %>\r\n                   <%= article.display_excerpt %>\r\n                  <p><a class=\"readmore\" href=\"<%= section_article_path(section_slug('blog_article'),article.slug)%>\">Read more &raquo;</a></p>\r\n                <% end %>\r\n                       </div>\r\n                       <!--/post-excerpt -->\r\n              </div>\r\n              <!--/content -->\r\n              <div class=\"clr\">\r\n              </div>\r\n              </div>\r\n              <div class=\"clr\">\r\n              </div>\r\n              <!--/box -->\r\n              <% end %>\r\n               <% else %>\r\n                 <h2>\r\n                   Author not found\r\n                 </h2>\r\n                 <% end %>\r\n                \r\n                <div class=\"clr\">\r\n                </div>\r\n                <div class=\"wp-pagenavi\">\r\n                  \r\n                    <span class=\"pages\">\r\n                   <%= pages(articles)%>\r\n                  </span>\r\n                    \r\n                  <%= paginate(articles)%>\r\n                  \r\n                  <div class=\"clr\">\r\n                  </div>\r\n                </div>\r\n              </div>\r\n              \r\n             <%= get_snippet('layout_recent_entries_box') %>\r\n                         <%= get_snippet('layout_categories_box') %>\r\n                           <%= get_snippet('layout_archives_box') %>\r\n                             <%= get_snippet('layout_footer') %>", :slug => "blog_tag")
Section.create(:id => 5, :name => "search", :content => "<%= get_snippet('layout_header') %>\r\n  <% search = Article.search_for(params[:article]) %>\r\n  \r\n    <% if !search.empty? %>\r\n      <% articles = search.page(params[:page]).per(12)%>\r\n  <% articles.each do |article|%>\r\n    <div class=\"box post\">\r\n      <div class=\"content\">\r\n        <div class=\"commets\">\r\n          <a href=\"<%= section_article_path(section_slug('blog_article'),article.slug)%>\" title=\"View Comments\">\r\n           <%= article.comments.size %>\r\n          </a>\r\n        </div>\r\n        <!-- commets -->\r\n        <div class=\"post-title\">\r\n          <h2>\r\n            <%= article.title %>\r\n            </h2>\r\n          </div>\r\n          <!--/post-title -->\r\n          <div class=\"social-links\">\r\n            <div class=\"post-date\">\r\n              <%= article.display_date%>\r\n                , In\r\n                <%= tags_with_links(article,'blog_tag') %>\r\n                , by \r\n               <%= author_with_links(article, 'blog_author')%>\r\n              </div>\r\n              <div class=\"clr\">\r\n                </div>\r\n              </div>\r\n              <!--/social-links -->\r\n              <div class=\"clr\">\r\n              </div>\r\n               <div class=\"article-content\">\r\n                 <% if article.excerpt.empty? %>\r\n                 <%= article.display_content %> \r\n                     <p>\r\n                          <a class=\"readmore\" href=\"<%= section_article_path(section_slug('blog_article'),article.slug)%>\">View Comments&raquo;</a>\r\n                     </p>\r\n                <% else %>\r\n                   <%= article.display_excerpt %>\r\n                  <p><a class=\"readmore\" href=\"<%= section_article_path(section_slug('blog_article'),article.slug)%>\">Read more &raquo;</a></p>\r\n                <% end %>\r\n                       </div>\r\n                       <!--/post-excerpt -->\r\n              </div>\r\n              <!--/content -->\r\n              <div class=\"clr\">\r\n              </div>\r\n              </div>\r\n              <div class=\"clr\">\r\n              </div>\r\n              <!--/box -->\r\n              <% end %>\r\n                             \r\n                <div class=\"clr\">\r\n                </div>\r\n                <div class=\"wp-pagenavi\">\r\n                  \r\n                   <span class=\"pages\">\r\n                   <%= pages(articles)%>\r\n                  </span>\r\n                    \r\n                  <%= paginate(articles)%>\r\n                  \r\n                      <% else %>\r\n                 <h2>\r\n                   Articles not found\r\n                 </h2>\r\n                 <% end %>\r\n                  <div class=\"clr\">\r\n                  </div>\r\n                </div>\r\n              </div>\r\n              \r\n             <%= get_snippet('layout_recent_entries_box') %>\r\n                         <%= get_snippet('layout_categories_box') %>\r\n                           <%= get_snippet('layout_archives_box') %>\r\n                             <%= get_snippet('layout_footer') %>", :slug => "search")
Section.create(:id => 6, :name => "blog_date", :content => "<%= get_snippet('layout_header') %>\r\n  <% month_year = Article.by_archive_date(params[:article]) %>\r\n  \r\n    <% if !month_year.empty? %>\r\n      <% articles = month_year.page(params[:page]).per(12)%>\r\n  <% articles.each do |article|%>\r\n    <div class=\"box post\">\r\n      <div class=\"content\">\r\n        <div class=\"commets\">\r\n          <a href=\"<%= section_article_path(section_slug('blog_article'),article.slug)%>\" title=\"View Comments\">\r\n           <%= article.comments.size %>\r\n          </a>\r\n        </div>\r\n        <!-- commets -->\r\n        <div class=\"post-title\">\r\n          <h2>\r\n            <%= article.title %>\r\n            </h2>\r\n          </div>\r\n          <!--/post-title -->\r\n          <div class=\"social-links\">\r\n            <div class=\"post-date\">\r\n              <%= article.display_date%>\r\n                , In\r\n                <%= tags_with_links(article,'blog_tag') %>\r\n                , by \r\n               <%= author_with_links(article, 'blog_author')%>\r\n              </div>\r\n              <div class=\"clr\">\r\n                </div>\r\n              </div>\r\n              <!--/social-links -->\r\n              <div class=\"clr\">\r\n              </div>\r\n               <div class=\"article-content\">\r\n                 <% if article.excerpt.empty? %>\r\n                 <%= article.display_content %> \r\n                     <p>\r\n                          <a class=\"readmore\" href=\"<%= section_article_path(section_slug('blog_article'),article.slug)%>\">View Comments&raquo;</a>\r\n                     </p>\r\n                <% else %>\r\n                   <%= article.display_excerpt %>\r\n                  <p><a class=\"readmore\" href=\"<%= section_article_path(section_slug('blog_article'),article.slug)%>\">Read more &raquo;</a></p>\r\n                <% end %>\r\n                       </div>\r\n                       <!--/post-excerpt -->\r\n              </div>\r\n              <!--/content -->\r\n              <div class=\"clr\">\r\n              </div>\r\n              </div>\r\n              <div class=\"clr\">\r\n              </div>\r\n              <!--/box -->\r\n              <% end %>\r\n               <% else %>\r\n                 <h2>\r\n                   Article not found\r\n                 </h2>\r\n                 <% end %>\r\n                \r\n                <div class=\"clr\">\r\n                </div>\r\n                <div class=\"wp-pagenavi\">\r\n                  \r\n                   <span class=\"pages\">\r\n                   <%= pages(articles)%>\r\n                  </span>\r\n                    \r\n                  <%= paginate(articles)%>\r\n                  \r\n                  <div class=\"clr\">\r\n                  </div>\r\n                </div>\r\n              </div>\r\n              \r\n             <%= get_snippet('layout_recent_entries_box') %>\r\n                         <%= get_snippet('layout_categories_box') %>\r\n                           <%= get_snippet('layout_archives_box') %>\r\n                             <%= get_snippet('layout_footer') %>", :slug => "blog_date")
