Snippet.create(:id => 2, :name => "layout_footer", :content => "<div id=\"page-bot\">\r\n  <div id=\"footer\"><a href=\"#\"><img src=\"/system/uploads/original_footer_logo.gif\" alt=\"footer_logo\" width=\"176\" height=\"128\" border=\"0\" /></a>\r\n    <div class=\"fr\">&copy; CompanyName. All Rights Reserved<br />\r\n      <b>(DT)</b> <a href=\"http://www.DreamTemplate.com\">Website Templates</a></div>\r\n    <div class=\"clr\"></div>\r\n  </div>\r\n  <!--/footer -->\r\n</div>\r\n<!--/page-bot -->\r\n</body>\r\n</html>\r\n")
Snippet.create(:id => 3, :name => "layout_recent_entries_box", :content => " <!--/centercol -->\r\n      <div id=\"rightcol\">\r\n        <div class=\"box ads\">\r\n          <div class=\"wtitle\">\r\n            <h2>Recent Entries</h2>\r\n          </div>\r\n          <div class=\"article-content\" style=\"padding-left:20px;padding-bottom:20px;\">\r\n           <ul>\r\n            <% Article.last(10).reverse.each do |article|%>\r\n            <li style=\"margin-bottom:8px;\"><a style=\"border-bottom: 1px dotted #ba0000;\" href=\"<%= section_article_path(section_slug('blog_article'),article.slug)%>\"><%= article.title%></a></li>\r\n           <% end %>\r\n           </ul>\r\n          </div>\r\n          <!--/content -->\r\n        </div>")
Snippet.create(:id => 4, :name => "layout_categories_box", :content => "<!--/box -->\r\n        <div class=\"box ads\">\r\n          <div class=\"wtitle\">\r\n             <h2>Categories</h2>\r\n          </div>\r\n          <div class=\"article-content\" style=\"padding-left:20px;padding-bottom:20px;\">\r\n           <ul>\r\n            <% Article.all_tags.sort.each do |tag|%>\r\n            <li style=\"margin-bottom:8px;\"><a style=\"border-bottom: 1px dotted #ba0000;\" href=\"<%= tag_with_path(tag,'blog_tag')%>\"><%= tag %></a> </li>\r\n           <% end %>\r\n           </ul>\r\n           </div>\r\n          <div class=\"clr\"></div>\r\n          <!--/content -->\r\n        </div>\r\n        <!--/box -->\r\n        <div class=\"clr\"></div>")
Snippet.create(:id => 5, :name => "layout_archives_box", :content => "<!--/box -->\r\n        <div class=\"box ads flickr\">\r\n          <div class=\"wtitle\">\r\n             <h2>Archives</h2>\r\n          </div>\r\n          <div class=\"article-content\" style=\"padding-left:20px;padding-bottom:20px;\">\r\n           <ul>\r\n            <% Article.by_archive.each do |date,articles|%>\r\n            <li style=\"margin-bottom:8px;\"><a style=\"border-bottom: 1px dotted #ba0000;\" href=\"<%= section_article_path(section_slug('blog_date'),date)%>\"><%= date %> (<%= articles.size %>)</a> </li>\r\n           <% end %>\r\n           </ul>\r\n           </div>\r\n          <div class=\"clr\"></div>\r\n          <!--/content -->\r\n        </div>\r\n      </div>\r\n      <!--/rightcol -->\r\n      <div class=\"clr\"></div>\r\n    </div>\r\n  </div>\r\n  <!--/columns -->\r\n</div>\r\n<!--/page -->")
Snippet.create(:id => 7, :name => "layout_header", :content => "<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\"\r\n\"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\" dir=\"ltr\" lang=\"en-US\">\r\n<head profile=\"http://gmpg.org/xfn/11\">\r\n<title>MasterThought Blog</title>\r\n<meta http-equiv=\"content-type\" content=\"text/html; charset=UTF-8\" />\r\n<meta name=\"description\" content=\"A Community Based WordPress Theme\" />\r\n<link rel=\"stylesheet\" type=\"text/css\" href=\"/system/uploads/original_style.css\" media=\"screen\" />\r\n<link rel=\"stylesheet\" type=\"text/css\" href=\"/system/uploads/original_superfish.css\" media=\"screen\" />\r\n<script type=\"text/javascript\" src=\"/system/uploads/original_jquery-1.3.2.min.js\"></script>\r\n<script type=\"text/javascript\" src=\"/system/uploads/original_hoverintent.js\"></script>\r\n<script type=\"text/javascript\" src=\"/system/uploads/original_superfish.js\"></script>\r\n<script type=\"text/javascript\" src=\"/system/uploads/original_jquery.cycle.all.min.js\"></script>\r\n<script type=\"text/javascript\" src=\"/system/uploads/original_script.js\"></script>\r\n\r\n<script type=\"text/javascript\" src=\"/assets/highlight/highlight.pack.js\"></script>\r\n<link rel=\"stylesheet\" href=\"/assets/highlight/styles/solarized_light.css\" type=\"text/css\"  />\r\n<script>hljs.initHighlightingOnLoad();</script>\r\n\r\n<!-- Start Of Script Generated By WP-PageNavi 2.40 -->\r\n<link rel=\"stylesheet\" href=\"/system/uploads/original_pagenavi-css.css\" type=\"text/css\" media=\"screen\" />\r\n<!-- End Of Script Generated By WP-PageNavi 2.40 -->\r\n</head>\r\n<body>\r\n<div id=\"page\">\r\n  <div id=\"header\">\r\n    <div class=\"logo\">\r\n      <h1><a href=\"#\" title=\"TemplateAccess Theme\">A TemplateAccess.com Theme</a></h1>\r\n    </div>\r\n    <!--/logo-->\r\n    <div class=\"search\">\r\n       <form method=\"post\" action=\"<%= search_url %>\">\r\n        <fieldset id=\"search\">\r\n          <span>\r\n          <input type=\"text\" value=\"Search...\" onclick=\"this.value='';\" name=\"s\" id=\"s\" />\r\n          <input type=\"hidden\" name=\"searchurl\" id=\"searchurl\" value=\"/section/search/article\" />\r\n          <input name=\"searchsubmit\" type=\"image\" src=\"/system/uploads/original_search.gif\" value=\"Go\" id=\"searchsubmit\" class=\"btn\"  />\r\n          </span>\r\n        </fieldset>\r\n     </form>\r\n    </div>\r\n    <!--/search-->\r\n    <div class=\"clr\"></div>\r\n    <div class=\"topnav\">\r\n      <ul>\r\n        <li class=\"current_page_item\"><a href=\"/\" title=\"Home\"><span>Home</span></a></li>\r\n        <li class=\"page_item page-item-2\"><a href=\"#\" title=\"About\"><span>About</span></a></li>\r\n        <li class=\"page_item page-item-6\"><a href=\"#\" title=\"Archives\"><span>Archives</span></a></li>\r\n        <li class=\"page_item page-item-4\"><a href=\"#\" title=\"Contact\"><span>Contact</span></a></li>\r\n      </ul>\r\n      <div class=\"rss\"> <a href=\"/feed.rss\" class=\"big\">Subscribe to our <span>RSS FEED</span></a> </div>\r\n      <div class=\"clr\"></div>\r\n    </div>\r\n    <!--/topnav-->\r\n    <img src=\"/system/uploads/original_menu_reize_bg.gif\" width=\"951\" height=\"42\" alt=\"images\" /> </div>\r\n  <!--/header -->\r\n  <div class=\"body\">\r\n    <div id=\"columns\">\r\n      <div id=\"centercol\">\r\n")
